import React, { useState, useEffect } from 'react';
import './css/ShowDetail.css';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import BookingForm from './BookingForm';


function ShowDetail() {
  const { id } = useParams();
  const [show, setShow] = useState(null);

  useEffect(() => {
    const fetchShowDetail = async () => {
      try {
        const response = await axios.get(`https://api.tvmaze.com/shows/${id}`);
        setShow(response.data);
      } catch (error) {
        console.error('Error fetching show detail:', error);
      }
    };

    fetchShowDetail();
  }, [id]);


  // const handleBookTicket = () => {
  //   // Assuming 'userDetails' is an object with user information
  //   const userDetails = {
  //     name: 'John Doe',
  //     email: 'john@example.com',
  //     // Add more details as needed
  //   };

  //   // Store user details in local storage
  //   localStorage.setItem('userDetails', JSON.stringify(userDetails));

  //   // Open a form or navigate to a booking page
  //   // You can implement your form or navigate to the booking page here
  //   // For simplicity, let's just log the action for now
  //   console.log('Booking form opened with pre-filled movie name:', show.name);
  // };


  return (
    <div className="showdetail-container">
      {show ? (
        <div className='show-content'>
          <img className="show-image" src={show.image ? show.image.original : 'No Image'} alt={`${show.name} image`} />
          <div className="show-text">
            <h2>{show.name}</h2>
            <p>{show.summary}</p>
            <div className="button-container">
              <Link to={`/book-ticket/${id}`}>
                <button className="book-ticket-button">Book Ticket</button>
              </Link>
              <Link to="/">
                <button className="back-button">Back to Show List</button>
              </Link>
            </div>
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}

    </div>
  );
}

export default ShowDetail;

















