import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './css/BookingPage.css';

function BookingPage() {
  const { id } = useParams();
  const [show, setShow] = useState(null);
  const [formData, setFormData] = useState({
    movieName: '',
    name: '',
    contact: '',
  });

  useEffect(() => {
    // Fetch show details again or use the stored details from local storage
    // ...

    // Assuming 'userDetails' is an object with user information
    const userDetails = JSON.parse(localStorage.getItem('userDetails'));

    // Fetch show details based on the show ID
    const fetchShowDetail = async () => {
      try {
        const response = await fetch(`https://api.tvmaze.com/shows/${id}`);
        const showData = await response.json();
        setShow(showData);
      } catch (error) {
        console.error('Error fetching show detail:', error);
      }
    };

    fetchShowDetail();

    // Set default values for form fields
    setFormData({
      movieName: id,
      name: userDetails?.name || '',
      contact: userDetails?.contact || '',
    });
  }, [id]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Store user details in local storage
    localStorage.setItem('userDetails', JSON.stringify(formData));

    // Handle form submission (you can implement your logic here)
    console.log('Form submitted:', formData);
  };

  return (
    <div className="container">
      {show ? (
        <div>
          <h1>Booking Page</h1>
          <div className="form-container">
            <label className="form-label" htmlFor="movieName">
              Movie Name
            </label>
            <input
              className="form-input"
              type="text"
              id="movieName"
              name="movieName"
              value={formData.movieName}
              readOnly
            />

            <label className="form-label" htmlFor="name">
              Name
            </label>
            <input
              className="form-input"
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />

            <label className="form-label" htmlFor="contact">
              Contact Detail
            </label>
            <input
              className="form-input"
              type="text"
              id="contact"
              name="contact"
              value={formData.contact}
              onChange={handleChange}
            />

            <button className="form-button" onClick={handleSubmit}>
              Submit
            </button>
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default BookingPage;
