import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import BookingForm from './BookingForm';
import axios from 'axios';
import './ShowDetail.css'; // Import the CSS file

function ShowDetail() {
  const { id } = useParams();
  const [show, setShow] = useState(null);

  useEffect(() => {
    const fetchShowDetail = async () => {
      try {
        const response = await axios.get(`https://api.tvmaze.com/shows/${id}`);
        setShow(response.data);
      } catch (error) {
        console.error('Error fetching show detail:', error);
      }
    };

    fetchShowDetail();
  }, [id]);

  return (
    <div className="container">
      {show ? (
        <div className="show-details">
          <h1 className="show-name">{show.name}</h1>
          <p className="show-summary">{show.summary}</p>
          <BookingForm movieName={show.name} />
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <div className="button-container">
        <Link to="/">
          <button className="back-button">Back to Show List</button>
        </Link>
      </div>
    </div>
  );
}

export default ShowDetail;
